function MoverBlock($curr='',$title='undefined',$body='null'){
// MoverBlock.JS
echo "<div id=\"mov_bl\" style=\"position:absolute;border: 1px solid aqua;background-color:white;\"><div id=\"mover\" style=\"width:100%;background-color:gray;cursor: move;color:white;\"><button onclick=\"Confirmexit();\">X</button>".$title."</div>".$body."</div>";
echo '<script type=\'text/javascript\'>
//DivMover JS
  var ie = 0;
  var op = 0;
  var ff = 0;
  var browser = navigator.userAgent;
  if (browser.indexOf("Opera") != -1) op = 1;
  else {
    if (browser.indexOf("MSIE") != -1) ie = 1;
    else {
      if (browser.indexOf("Firefox") != -1) ff = 1;
    }
  }
  var block = document.getElementById("mover");
  var block1 = document.getElementById("mov_bl");
  delta_x = 0;
  delta_y = 0;
  
  block.onmousedown = saveXY;
  if (op || ff) {
    block.addEventListener("onmousedown", saveXY, false);
  }
  document.onmouseup = clearXY;
 
  function saveXY(obj_event) {
  
    if (obj_event) {
      x = obj_event.pageX;
      y = obj_event.pageY;
    }
    else {
      x = window.event.clientX;
      y = window.event.clientY;
      if (ie) {
        y -= 2;
        x -= 2;
      }
    }
   
    x_block = block1.offsetLeft;
    y_block = block1.offsetTop;
   
    delta_x = x_block - x;
    delta_y = y_block - y;
 
    document.onmousemove = moveBlock;
    if (op || ff)
      document.addEventListener("onmousemove", moveBlock, false);
  }
  function clearXY() {
    document.onmousemove = null; 
  }
  function moveBlock(obj_event) {
   
    if (obj_event) {
      x = obj_event.pageX;
      y = obj_event.pageY;
    }
    else {
      x = window.event.clientX;
      y = window.event.clientY;
      if (ie) {
        y -= 2;
        x -= 2;
      }
    }
  
    new_x = delta_x + x;
    new_y = delta_y + y;
    block1.style.top = new_y + "px";
    block1.style.left = new_x + "px";
  }
// Created by Samarine 
// TG: @samarine_w
function Confirmexit(){
window.location.href="'.str_replace("\\", "/", $curr).'";
}
</script>
';
